def longestPalindrome(s: str) -> str:
    
    current_sub_size = 2
    sub_string = ""
    if current_sub_size > len(s):
        return s[0]
    
    flag = True

    while (current_sub_size <= len(s)):

        find = False

        for j in range(len(s)):

            inside_sub_string = s[j:j+current_sub_size]
            if inside_sub_string[0] == inside_sub_string[-1] and inside_sub_string == inside_sub_string[::-1] and len(inside_sub_string)> len(sub_string) :
                sub_string = inside_sub_string
                find = True
                current_sub_size +=1
                break
            
        if (find == False):
            current_sub_size+=1

    return sub_string
    
    
    
